import React, { useEffect, useState } from "react";
import "./Form.css";

function Form() {
  let [name, setName] = useState("");
  let [email, setEmail] = useState("");
  let [password, setPassword] = useState("");
  let [myArr, setArr] = useState([]);
  let [status, setStatus] = useState(false);
  let [para,setPara] = useState("Try it free 7 days then ₹180/mo. thereafter");
  let p1 = "Try it free 7 days then ₹180/mo. thereafter";
  let p2 = "You have successfully subscribed to our plan ✔️";
  let paragraphs = [];
  paragraphs.push(p1);
  paragraphs.push(p2);

  let deleteSkill = (value) => {
    setArr((oldValues) => {
      return oldValues.filter((skill) => skill !== value);
    });
  };

  function changeButtonState() {
    if (
      name.trim().length > 0 &&
      email.trim().length > 0 &&
      password.length > 0 &&
      myArr.length >= 1
    ) {
      setStatus(true);
      console.log(true);
    }
  }

  function resetForm() {
    setName("");
    setEmail("");
    setPassword("");
    setArr([]);
    setStatus(false);
  }

  useEffect(() => {
    if (name.length > 0 && email.length > 0 && password.length > 0 && myArr.length >= 1) {
      setStatus(true);
      console.log(true);
      setPara("You have successfully subscribed to our plan ✔️");
    } else {
      setStatus(false);
    }
  }, [name.length, email.length, password.length, myArr.length]);
  return (
    <>
      <div className="holder">
        <p className="para-right">{para}</p>
      </div>
      <div className="form">
        <div className="input-fields">
          <input
            autoFocus="true"
            type="text"
            className="name"
            placeholder="Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="email"
            className="email"
            placeholder="Email Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            className="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <select
            name="skills"
            className="skills"
            onChange={(e) => {
              let temp = e.target.value;
              if (!myArr.includes(temp)) setArr((arr) => [...arr, temp]);
            }}
          >
            <option value="" selected disabled>
              Choose your skills
            </option>
            <option value="HTML">HTML</option>
            <option value="CSS">CSS</option>
            <option value="JS">JS</option>
            <option value="REACT">REACT</option>
          </select>

          <div className="skills-selected">
            {myArr.map((skill) => (
              <>
                <div className="skill">
                  <p key={skill} className="skill-para">
                    {skill}
                  </p>
                  <p className="cross" onClick={() => deleteSkill(skill)}>
                    X
                  </p>
                </div>
              </>
            ))}
          </div>
          <button
            className="btn"
            onChange={changeButtonState}
            disabled={!status}
            onClick={resetForm}
          >
            CLAIM YOUR FREE TRIAL
          </button>
        </div>
        <p className="tAndc">
          By clicking the button you are agreeing to our{" "}
          <span className="para-tc"> Terms and Services</span>
        </p>
      </div>
    </>
  );
}

export default Form;
