import React, {useState, useEffect} from 'react'
import './Form.css';


function Form() {
  const arrayOfSkills = []
  let [name,setName] = useState('');
  let [email,setEmail] = useState('');
  let [password,setPassword] = useState('');
  let [skills,setSkills] = useState('');
  // let [arrayOfSkills, setArrayOfSkills] = useState([]);

  // useEffect(() => {
  //   setArrayOfSkills(skills);
  //   console.log(arrayOfSkills);
  // },[skills, arrayOfSkills])
  function skillsSection(){
    // console.log(skills);
    arrayOfSkills.push(skills);
    console.log(arrayOfSkills);
  }
  return (
    <>
    <div className="form">
        <div className="input-fields">
            <input type="text" className="name" placeholder='Your Name' 
            value={name} onChange={(e) => setName(e.target.value)}
            />
            <input type="email" className="email" placeholder='Email Address'
            value={email} onChange={(e) => setEmail(e.target.value)}
            />
            <input type="password" className="password" placeholder='Password'
            value={password} onChange = {(e) => setPassword(e.target.value)}
            />
            <select name="skills" className="skills"
            value={skills} onChange = {(e) => setSkills(e.target.value)} onG = {skillsSection}
            >
            <option value="" selected disabled>Choose your skills</option>
                <option value="HTML">HTML</option>
                <option value="CSS">CSS</option>
                <option value="JS">JS</option>
                <option value="REACT">REACT</option>
            </select>
            <div className="skills-selected">
                {/* the skills will come here */}
                {console.log(name)}
                {console.log(email)}
                {console.log(password)}
                {/* {console.log(skills)} */}
                {/* {console.log(arrayOfSkills)} */}
            </div>
            <button disabled="disabled" className="btn">CLAIM YOUR FREE TRIAL</button>
        </div>
        <p className="tAndc">By clicking the button you are agreeing to our <span className="para-tc"> Terms and Services</span></p>
    </div>
    </>
  )
}

export default Form;
